import React, { useState, useEffect } from 'react';
import { Upload, AlertTriangle } from 'lucide-react';
import './App.css';

// ÏóÖÎ°úÎìú Ïª¥Ìè¨ÎÑåÌä∏
const FileUpload = ({ isLoading, onFileSelect }) => {
  const fileInputRef = React.useRef(null);

  const handleClick = () => {
    fileInputRef.current.click();
  };

  return (
    <div className="upload-section" onClick={handleClick}>
      <input
        type="file"
        ref={fileInputRef}
        accept="image/*"
        onChange={onFileSelect}
        disabled={isLoading}
        style={{ display: 'none' }}
      />
      <Upload className="w-12 h-12 text-gray-400 mb-4" />
      <p className="text-lg font-medium text-gray-900 mb-2">
        ÏãùÎ¨º Ïù¥ÎØ∏ÏßÄ ÏóÖÎ°úÎìú
      </p>
      <p className="text-sm text-gray-500">
        Ïù¥ÎØ∏ÏßÄÎ•º ÌÅ¥Î¶≠ÌïòÍ±∞ÎÇò ÎìúÎûòÍ∑∏ÌïòÏó¨ ÏóÖÎ°úÎìúÌïòÏÑ∏Ïöî
      </p>
    </div>
  );
};

// Î°úÎî© Ïª¥Ìè¨ÎÑåÌä∏
const LoadingSpinner = () => (
  <div className="loading">
    <div className="loading-spinner" />
    <p className="text-gray-600">ÏãùÎ¨ºÏùÑ Î∂ÑÏÑùÌïòÍ≥† ÏûàÏäµÎãàÎã§...</p>
  </div>
);

// LoadingCard Ïª¥Ìè¨ÎÑåÌä∏ Ï∂îÍ∞Ä
const LoadingCard = () => (
  <div className="loading-card">
    <div className="loading-content">
      <div className="loading-spinner"></div>
      <div className="loading-text">
        <h3>ÏãùÎ¨ºÏùÑ Î∂ÑÏÑùÌïòÍ≥† ÏûàÏäµÎãàÎã§</h3>
        <p>Ïû†ÏãúÎßå Í∏∞Îã§Î†§Ï£ºÏÑ∏Ïöî...</p>
      </div>
      <div className="loading-steps">
        <div className="step completed">
          <span className="step-number">1</span>
          <span className="step-text">Ïù¥ÎØ∏ÏßÄ ÏóÖÎ°úÎìú ÏôÑÎ£å</span>
        </div>
        <div className="step active">
          <span className="step-number">2</span>
          <span className="step-text">AI Î∂ÑÏÑù ÏßÑÌñâÏ§ë</span>
        </div>
        <div className="step">
          <span className="step-number">3</span>
          <span className="step-text">Í≤∞Í≥º ÏÉùÏÑ±</span>
        </div>
      </div>
    </div>
  </div>
);


// ÏóêÎü¨ Î©îÏãúÏßÄ Ïª¥Ìè¨ÎÑåÌä∏
const ErrorMessage = ({ message }) => (
  <div className="error">
    <div className="flex items-center gap-2 mb-2">
      <AlertTriangle className="w-5 h-5" />
      <p className="font-medium">Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§</p>
    </div>
    <p className="text-sm">{message}</p>
  </div>
);

// Ïù¥ÎØ∏ÏßÄ ÌîÑÎ¶¨Î∑∞ Ïª¥Ìè¨ÎÑåÌä∏
const ImagePreview = ({ image }) => (
  <div className="image-preview-wrapper">
    <div className="image-preview-container">
      <div className="image-preview-header">
        <h3>ÏÑ†ÌÉùÎêú Ïù¥ÎØ∏ÏßÄ</h3>
      </div>
      <div className="image-preview-content">
        <img
          src={URL.createObjectURL(image)}
          alt="ÏÑ†ÌÉùÎêú ÏãùÎ¨º"
        />
      </div>
    </div>
  </div>
);

// ÏãùÎ¨º Ï†ïÎ≥¥ Ïπ¥Îìú Ïª¥Ìè¨ÎÑåÌä∏
const PlantCard = ({ name, scientificName, waterFrequency, temperature, humidity, difficulty, features, precautions }) => {
  return (
    <div className="card">
      <h2 className="text-2xl font-bold text-gray-900 mb-2">
        {name}
      </h2>
      {scientificName !== "Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§" && (
        <p className="text-gray-500 italic mb-4">{scientificName}</p>
      )}
      
      <div className="tags">
        <span className="tag related">Í¥ÄÏóΩÏãùÎ¨º</span>
        <span className="tag level">{difficulty}</span>
      </div>

      <div className="info-section">
        {waterFrequency !== "Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§" && (
          <div className="info-item">
            <div className="icon">üíß</div>
            <div>
              <p className="label">Î¨ºÏ£ºÍ∏∞</p>
              <p className="value">{waterFrequency}</p>
            </div>
          </div>
        )}
        {temperature !== "Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§" && (
          <div className="info-item">
            <div className="icon">üå°Ô∏è</div>
            <div>
              <p className="label">Ï†ÅÏ†ï Ïò®ÎèÑ</p>
              <p className="value">{temperature}</p>
            </div>
          </div>
        )}
        {humidity !== "Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§" && (
          <div className="info-item">
            <div className="icon">üí®</div>
            <div>
              <p className="label">ÏäµÎèÑ</p>
              <p className="value">{humidity}</p>
            </div>
          </div>
        )}
      </div>

      {features.length > 0 && (
        <div className="features-section">
          <h3 className="section-title">
            <span>‚ú®</span>
            <span>ÌäπÏßï</span>
          </h3>
          <ul>
            {features.map((feature, index) => (
              <li key={index}>{feature}</li>
            ))}
          </ul>
        </div>
      )}

      {precautions.length > 0 && (
        <div className="precautions-section">
          <h3 className="section-title">
            <span>‚ö†Ô∏è</span>
            <span>Ï£ºÏùòÏÇ¨Ìï≠</span>
          </h3>
          <ul>
            {precautions.map((precaution, index) => (
              <li key={index}>{precaution}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

// App Ïª¥Ìè¨ÎÑåÌä∏
function App() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [plantInfo, setPlantInfo] = useState(null);
  
  const API_KEY = process.env.REACT_APP_GEMINI_API_KEY;

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedImage(file);
      setError(null);
      analyzePlant(file);
    }
  };

  const parsePlantInfo = (text) => {
    try {
      const plantData = {
        name: "Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§",
        scientificName: "Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§",
        difficulty: "Ï¥àÍ∏â",
        waterFrequency: "Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§",
        temperature: "Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§",
        humidity: "Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§",
        features: [],
        precautions: []
      };

      const lines = text.split('\n');
      let currentSection = '';

      lines.forEach(line => {
        line = line.trim();
        if (line.includes('Ïù¥Î¶Ñ:') || line.includes('ÏãùÎ¨º:')) {
          plantData.name = line.split(':')[1].trim();
        } else if (line.includes('ÌïôÎ™Ö:')) {
          plantData.scientificName = line.split(':')[1].trim();
        } else if (line.toLowerCase().includes('Î¨ºÏ£ºÍ∏∞:')) {
          plantData.waterFrequency = line.split(':')[1].trim();
        } else if (line.includes('Ïò®ÎèÑ:')) {
          plantData.temperature = line.split(':')[1].trim();
        } else if (line.includes('ÏäµÎèÑ:')) {
          plantData.humidity = line.split(':')[1].trim();
        } else if (line.includes('ÌäπÏßï:')) {
          currentSection = 'features';
        } else if (line.includes('Ï£ºÏùòÏÇ¨Ìï≠:')) {
          currentSection = 'precautions';
        } else if (line && currentSection) {
          if (line.startsWith('-') || line.startsWith('‚Ä¢')) {
            line = line.substring(1).trim();
          }
          if (line && currentSection === 'features') {
            plantData.features.push(line);
          } else if (line && currentSection === 'precautions') {
            plantData.precautions.push(line);
          }
        }
      });

      return plantData;
    } catch (error) {
      console.error('Parsing error:', error);
      throw new Error('ÏãùÎ¨º Ï†ïÎ≥¥ ÌååÏã± Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    }
  };

  const analyzePlant = async (file) => {
    if (!API_KEY) {
      setError('API ÌÇ§Í∞Ä ÏÑ§Ï†ïÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      
      reader.onloadend = async () => {
        try {
          const base64Image = reader.result.split(',')[1];
          
          const requestBody = {
            contents: [{
              parts: [
                {
                  text: `Ïù¥ ÏãùÎ¨ºÏùò Ï†ïÎ≥¥Î•º Îã§Ïùå ÌòïÏãùÏúºÎ°ú ÏïåÎ†§Ï£ºÏÑ∏Ïöî:
Ïù¥Î¶Ñ:
ÌïôÎ™Ö:
Î¨ºÏ£ºÍ∏∞:
Ïò®ÎèÑ:
ÏäµÎèÑ:
ÌäπÏßï:
- ÌäπÏßï1
- ÌäπÏßï2
Ï£ºÏùòÏÇ¨Ìï≠:
- Ï£ºÏùòÏÇ¨Ìï≠1
- Ï£ºÏùòÏÇ¨Ìï≠2`
                },
                {
                  inlineData: {
                    mimeType: "image/jpeg",
                    data: base64Image
                  }
                }
              ]
            }]
          };

          const response = await fetch(
            `https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash-latest:generateContent?key=${API_KEY}`,
            {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
              },
              body: JSON.stringify(requestBody)
            }
          );

          if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
          }

          const data = await response.json();
          
          if (data.candidates && data.candidates[0] && data.candidates[0].content) {
            const text = data.candidates[0].content.parts[0].text;
            console.log('API Response:', text);
            
            const plantData = parsePlantInfo(text);
            setPlantInfo(plantData);
          } else {
            throw new Error('API ÏùëÎãµ ÌòïÏãùÏù¥ Ïò¨Î∞îÎ•¥ÏßÄ ÏïäÏäµÎãàÎã§.');
          }
          
        } catch (error) {
          console.error('Error:', error);
          setError(`Ïù¥ÎØ∏ÏßÄ Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§: ${error.message}`);
          setPlantInfo(null);
        }
      };
    } catch (error) {
      console.error('Error:', error);
      setError('Ïù¥ÎØ∏ÏßÄ Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
      setPlantInfo(null);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="App">
      <h1 className="text-center text-3xl font-bold mb-8">Plant Identifier</h1>
      
      <FileUpload
        isLoading={isLoading}
        onFileSelect={handleImageUpload}
      />

      {selectedImage && <ImagePreview image={selectedImage} />}
      
      {isLoading && <LoadingSpinner />}
      
      {error && <ErrorMessage message={error} />}
      
      {plantInfo && !error && <PlantCard {...plantInfo} />}
    </div>
  );
}

export default App;
