import React, { useState } from 'react';
import './App.css';

const API_KEY = process.env.REACT_APP_GEMINI_API_KEY;

function App() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [result, setResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    setSelectedImage(file);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    
    if (!selectedImage) {
      alert('이미지를 선택해주세요');
      return;
    }
    
    if (!API_KEY) {
      alert('API 키가 설정되지 않았습니다');
      return;
    }

    try {
      setIsLoading(true);
      setResult('이미지 분석 중...');
      
      const reader = new FileReader();
      reader.readAsDataURL(selectedImage);
      
      reader.onloadend = async () => {
        try {
          const base64Image = reader.result.split(',')[1];
          
          const requestBody = {
            contents: [{
              parts: [
                { text: "이 식물이나 과일이 무엇인지 한국어로 설명해주세요." },
                {
                  inline_data: {
                    mime_type: selectedImage.type,
                    data: base64Image
                  }
                }
              ]
            }]
          };

          // 요청 내용 확인
          console.log('Request Body:', requestBody);

          const response = await fetch(`https://generativelanguage.googleapis.com/v1beta/models/gemini-pro-vision:generateContent?key=${API_KEY}`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(requestBody)
          });

          const data = await response.json();
          
          // API 응답 확인
          console.log('API Response:', data);

          if (data.candidates && data.candidates[0]?.content?.parts?.[0]?.text) {
            setResult(data.candidates[0].content.parts[0].text);
          } else {
            console.error('Unexpected API response:', data);
            setResult('API 응답 형식이 올바르지 않습니다.');
          }
        } catch (error) {
          console.error('Error in onloadend:', error);
          setResult('이미지 처리 중 오류가 발생했습니다.');
        }
      };
      
    } catch (error) {
      console.error('Error:', error);
      setResult('이미지 분석 중 오류가 발생했습니다.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>식물 식별기</h1>
      {!API_KEY && (
        <div className="error-message" style={{ color: 'red', margin: '10px' }}>
          ⚠️ API 키가 설정되지 않았습니다. .env 파일을 확인해주세요.
        </div>
      )}
      <form onSubmit={handleSubmit}>
        <input
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
        />
        <button type="submit">분석하기</button>
      </form>
      
      {selectedImage && (
        <div className="preview">
          <h3>선택된 이미지:</h3>
          <img
            src={URL.createObjectURL(selectedImage)}
            alt="선택된 식물"
            style={{ maxWidth: '300px' }}
          />
        </div>
      )}

      {result && (
        <div className="result">
          <h3>분석 결과:</h3>
          <p>{result}</p>
        </div>
      )}
    </div>
  );
}

export default App;
