import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [result, setResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  
  const API_KEY = process.env.REACT_APP_GEMINI_API_KEY;

  useEffect(() => {
    if (selectedImage) {
      handleSubmit();
    }
  }, [selectedImage]);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedImage(file);
      setResult(null);
    }
  };

  const handleSubmit = async (e) => {
    if (e) e.preventDefault();
    
    if (!selectedImage) {
      alert('Ïù¥ÎØ∏ÏßÄÎ•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî');
      return;
    }
    
    if (!API_KEY) {
      alert('API ÌÇ§Í∞Ä ÏÑ§Ï†ïÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§');
      return;
    }

    setIsLoading(true);
    setResult('Ïù¥ÎØ∏ÏßÄ Î∂ÑÏÑù Ï§ë...');

    try {
      const reader = new FileReader();
      reader.readAsDataURL(selectedImage);
      
      reader.onloadend = async () => {
        try {
          const base64Image = reader.result.split(',')[1];
          const cleanBase64 = btoa(atob(base64Image));
          
          const requestBody = {
            contents: [{
              parts: [
                {
                  text: "Ïù¥ ÏãùÎ¨ºÏù¥ÎÇò ÍΩÉÏù¥ Î¨¥ÏóáÏù∏ÏßÄ ÌïúÍµ≠Ïñ¥Î°ú ÏÑ§Î™ÖÌï¥Ï£ºÏÑ∏Ïöî."
                },
                {
                  inlineData: {
                    mimeType: "image/jpeg",
                    data: cleanBase64
                  }
                }
              ]
            }]
          };

          const response = await fetch(
            `https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash-latest:generateContent?key=${API_KEY}`,
            {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
              },
              body: JSON.stringify(requestBody)
            }
          );

          if (!response.ok) {
            const errorData = await response.text();
            throw new Error(`HTTP error! status: ${response.status}, details: ${errorData}`);
          }

          const data = await response.json();
          setResult(data.candidates[0].content.parts[0].text);
          
        } catch (error) {
          console.error('Error:', error);
          setResult(`Ïù¥ÎØ∏ÏßÄ Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§: ${error.message}`);
        }
      };
    } catch (error) {
      console.error('Error:', error);
      setResult('Ïù¥ÎØ∏ÏßÄ Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    } finally {
      setIsLoading(false);
    }
  };

  const PlantCard = ({ name, scientificName, waterFrequency, temperature, humidity, difficulty, features, precautions }) => {
    return (
      <div className="card">
        <h2>üåø {name} <span style={{ fontSize: '14px', color: '#888' }}>({difficulty})</span></h2>
        <h3>{scientificName}</h3>
        <p>üíß Î¨ºÏ£ºÍ∏∞: {waterFrequency}</p>
        <p>üå°Ô∏è Ï†ÅÏ†ï Ïò®ÎèÑ: {temperature}</p>
        <p>üí® ÏäµÎèÑ: {humidity}</p>
        <h4>ÌäπÏßï</h4>
        <ul>
          {features.map((feature, index) => (
            <li key={index}>‚ú® {feature}</li>
          ))}
        </ul>
        <h4>Ï£ºÏùòÏÇ¨Ìï≠</h4>
        <ul>
          {precautions.map((precaution, index) => (
            <li key={index}>‚ö†Ô∏è {precaution}</li>
          ))}
        </ul>
      </div>
    );
  };

  return (
    <div className="App">
      <h1>Plant Identifier</h1>
      
      <form onSubmit={handleSubmit}>
        <input
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
          disabled={isLoading}
        />
        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Î∂ÑÏÑù Ï§ë...' : 'Î∂ÑÏÑùÌïòÍ∏∞'}
        </button>
      </form>

      {selectedImage && (
        <div className="preview">
          <h3>ÏÑ†ÌÉùÎêú Ïù¥ÎØ∏ÏßÄ:</h3>
          <img
            src={URL.createObjectURL(selectedImage)}
            alt="ÏÑ†ÌÉùÎêú ÏãùÎ¨º"
            style={{ maxWidth: '300px' }}
          />
        </div>
      )}

      {result && (
        <div className="result">
          <h3>Î∂ÑÏÑù Í≤∞Í≥º:</h3>
          <p>{result}</p>
        </div>
      )}

      <PlantCard 
        name="Î™¨Ïä§ÌÖåÎùº"
        scientificName="Monstera deliciosa"
        difficulty="Ïâ¨ÏõÄ"
        waterFrequency="Ï£º 1-2Ìöå"
        temperature="18-30¬∞C"
        humidity="60-80%"
        features={[
          "ÌÅ∞ ÌÅ¨Í∏∞Ïùò ÏûéÏù¥ ÌäπÏßïÏ†ÅÏûÖÎãàÎã§.",
          "Í≥µÍ∏∞Ï†ïÌôî Îä•Î†•Ïù¥ Îõ∞Ïñ¥ÎÇ©ÎãàÎã§.",
          "Ïó¥ÎåÄÏö∞Î¶ºÏù¥ ÏõêÏÇ∞ÏßÄÏûÖÎãàÎã§."
        ]}
        precautions={[
          "ÏßÅÏÇ¨Í¥ëÏÑ†ÏùÄ ÌîºÌï¥Ï£ºÏÑ∏Ïöî.",
          "Í≥ºÏäµÏóê Ï£ºÏùòÌïòÏÑ∏Ïöî.",
          "Ïñ¥Î¶∞ÏïÑÏù¥ÏôÄ Î∞òÎ†§ÎèôÎ¨ºÏù¥ ÏÑ≠Ï∑®ÌïòÏßÄ ÏïäÎèÑÎ°ù Ï£ºÏùòÌïòÏÑ∏Ïöî."
        ]}
      />
    </div>
  );
}

export default App;
