import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [result, setResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  
  const API_KEY = process.env.REACT_APP_GEMINI_API_KEY;

  useEffect(() => {
    if (selectedImage) {
      handleSubmit();
    }
  }, [selectedImage]);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedImage(file);
      setResult(null);
    }
  };

  const handleSubmit = async (e) => {
    if (e) e.preventDefault();
    
    if (!selectedImage) {
      alert('이미지를 선택해주세요');
      return;
    }
    
    if (!API_KEY) {
      alert('API 키가 설정되지 않았습니다');
      return;
    }

    setIsLoading(true);
    setResult('이미지 분석 중...');

    try {
      const reader = new FileReader();
      reader.readAsDataURL(selectedImage);
      
      reader.onloadend = async () => {
        try {
          const base64Image = reader.result.split(',')[1];
          const cleanBase64 = btoa(atob(base64Image));
          
          const requestBody = {
            contents: [{
              parts: [
                {
                  text: "이 식물이나 꽃이 무엇인지 한국어로 설명해주세요."
                },
                {
                  inlineData: {
                    mimeType: "image/jpeg",
                    data: cleanBase64
                  }
                }
              ]
            }]
          };

          const response = await fetch(
            `https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash-latest:generateContent?key=${API_KEY}`,
            {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
              },
              body: JSON.stringify(requestBody)
            }
          );

          if (!response.ok) {
            const errorData = await response.text();
            throw new Error(`HTTP error! status: ${response.status}, details: ${errorData}`);
          }

          const data = await response.json();
          setResult(data.candidates[0].content.parts[0].text);
          
        } catch (error) {
          console.error('Error:', error);
          setResult(`이미지 처리 중 오류가 발생했습니다: ${error.message}`);
        }
      };
    } catch (error) {
      console.error('Error:', error);
      setResult('이미지 처리 중 오류가 발생했습니다.');
    } finally {
      setIsLoading(false);
    }
  };

  const PlantCard = ({ name, scientificName, waterFrequency, temperature, humidity, difficulty, features, precautions }) => {
    return (
      <div className="card">
        <h2>🌿 {name} <span style={{ fontSize: '14px', color: '#888' }}>({difficulty})</span></h2>
        <h3>{scientificName}</h3>
        <p>💧 물주기: {waterFrequency}</p>
        <p>🌡️ 적정 온도: {temperature}</p>
        <p>💨 습도: {humidity}</p>
        <h4>특징</h4>
        <ul>
          {features.map((feature, index) => (
            <li key={index}>✨ {feature}</li>
          ))}
        </ul>
        <h4>주의사항</h4>
        <ul>
          {precautions.map((precaution, index) => (
            <li key={index}>⚠️ {precaution}</li>
          ))}
        </ul>
      </div>
    );
  };

  return (
    <div className="App">
      <h1>Plant Identifier</h1>
      
      <form onSubmit={handleSubmit}>
        <input
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
          disabled={isLoading}
        />
        <button type="submit" disabled={isLoading}>
          {isLoading ? '분석 중...' : '분석하기'}
        </button>
      </form>

      {selectedImage && (
        <div className="preview">
          <h3>선택된 이미지:</h3>
          <img
            src={URL.createObjectURL(selectedImage)}
            alt="선택된 식물"
            style={{ maxWidth: '300px' }}
          />
        </div>
      )}

      {result && (
        <div className="result">
          <h3>분석 결과:</h3>
          <p>{result}</p>
        </div>
      )}

      <PlantCard 
        name="몬스테라"
        scientificName="Monstera deliciosa"
        difficulty="쉬움"
        waterFrequency="주 1-2회"
        temperature="18-30°C"
        humidity="60-80%"
        features={[
          "큰 크기의 잎이 특징적입니다.",
          "공기정화 능력이 뛰어납니다.",
          "열대우림이 원산지입니다."
        ]}
        precautions={[
          "직사광선은 피해주세요.",
          "과습에 주의하세요.",
          "어린아이와 반려동물이 섭취하지 않도록 주의하세요."
        ]}
      />
    </div>
  );
}

export default App;
