        temperature: "Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§",
        humidity: "Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§",
        features: [],
        precautions: []
      };

      const lines = text.split('\n');
      let currentSection = '';

      lines.forEach(line => {
        line = line.trim();
        if (line.includes('Ïù¥Î¶Ñ:') || line.includes('ÏãùÎ¨º:')) {
          plantData.name = line.split(':')[1].trim();
        } else if (line.includes('ÌïôÎ™Ö:')) {
