  // analyzePlant Ìï®Ïàò ÏàòÏ†ï
  const analyzePlant = async (file) => {
    if (!API_KEY) {
      setError('API ÌÇ§Í∞Ä ÏÑ§Ï†ïÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      // ÌååÏùº ÌÅ¨Í∏∞ Í≤ÄÏ¶ù Ï∂îÍ∞Ä
      if (file.size > 5 * 1024 * 1024) {
        throw new Error('ÌååÏùº ÌÅ¨Í∏∞Îäî 5MB Ïù¥ÌïòÏó¨Ïïº Ìï©ÎãàÎã§.');
      }

      const reader = new FileReader();
      
      // FileReader ÏóêÎü¨ Ï≤òÎ¶¨ Ï∂îÍ∞Ä
      reader.onerror = () => {
        setError('ÌååÏùº ÏùΩÍ∏∞ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
        setIsLoading(false);
      };

      reader.readAsDataURL(file);
      
      reader.onloadend = async () => {
        try {
          const base64Image = reader.result.split(',')[1];
          
          const requestBody = {
            contents: [{
              parts: [
                {
                  text: `Ïù¥ ÏãùÎ¨ºÏùò Ï†ïÎ≥¥Î•º Îã§Ïùå ÌòïÏãùÏúºÎ°ú ÏïåÎ†§Ï£ºÏÑ∏Ïöî:
Ïù¥Î¶Ñ:
ÌïôÎ™Ö:
Î¨ºÏ£ºÍ∏∞:
Ïò®ÎèÑ:
ÏäµÎèÑ:
ÌäπÏßï:
- ÌäπÏßï1
- ÌäπÏßï2
Ï£ºÏùòÏÇ¨Ìï≠:
- Ï£ºÏùòÏÇ¨Ìï≠1
- Ï£ºÏùòÏÇ¨Ìï≠2`
                },
                {
                  inlineData: {
                    mimeType: "image/jpeg",
                    data: base64Image
                  }
                }
              ]
            }]
          };

          const response = await fetch(
            `https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash-latest:generateContent?key=${API_KEY}`,
            {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
              },
              body: JSON.stringify(requestBody)
            }
          );

          if (!response.ok) {
            throw new Error(`ÏÑúÎ≤Ñ Ïò§Î•ò: ${response.status}`);
          }

          const data = await response.json();
          
          if (data.candidates?.[0]?.content) {
            const text = data.candidates[0].content.parts[0].text;
            console.log('API Response:', text);
            
            const plantData = parsePlantInfo(text);
            setPlantInfo(plantData);
          } else {
            throw new Error('API ÏùëÎãµ ÌòïÏãùÏù¥ Ïò¨Î∞îÎ•¥ÏßÄ ÏïäÏäµÎãàÎã§.');
          }
          
        } catch (error) {
          console.error('Error:', error);
          setError(`Ïù¥ÎØ∏ÏßÄ Î∂ÑÏÑù Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§: ${error.message}`);
          setPlantInfo(null);
        }
      };
    } catch (error) {
      console.error('Error:', error);
      setError(error.message || 'Ïù¥ÎØ∏ÏßÄ Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
      setPlantInfo(null);
    } finally {
