        )}
        {precautions.length > 0 && (
          <div className="precautions-section">
            <h4>Ï£ºÏùòÏÇ¨Ìï≠</h4>
            <ul>
              {precautions.map((precaution, index) => (
                <li key={index}>‚ö†Ô∏è {precaution}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="App">
      <h1>Plant Identifier</h1>
      
      <FileUpload
        isLoading={isLoading}
