import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [result, setResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [plantInfo, setPlantInfo] = useState(null);
  const [error, setError] = useState(null);
  
  const API_KEY = process.env.REACT_APP_GEMINI_API_KEY;

  useEffect(() => {
    if (selectedImage) {
      handleSubmit();
    }
  }, [selectedImage]);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedImage(file);
      setResult(null);
      setError(null);
    }
  };

  const parsePlantInfo = (text) => {
    try {
      // Í∏∞Î≥∏ Ï†ïÎ≥¥ Íµ¨Ï°∞ Ï¥àÍ∏∞Ìôî
      const plantData = {
        name: "Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§",
        scientificName: "Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§",
        difficulty: "Ï¥àÍ∏â",
        waterFrequency: "Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§",
        temperature: "Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§",
        humidity: "Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§",
        features: [],
        precautions: []
      };

      // ÌÖçÏä§Ìä∏ÏóêÏÑú Ï†ïÎ≥¥ Ï∂îÏ∂ú
      const lines = text.split('\n');
      let currentSection = '';

      lines.forEach(line => {
        line = line.trim();
        
        // Ïù¥Î¶Ñ Ï∂îÏ∂ú
        if (line.includes('Ïù¥Î¶Ñ:') || line.includes('ÏãùÎ¨º:')) {
          plantData.name = line.split(':')[1].trim();
        }
        // ÌïôÎ™Ö Ï∂îÏ∂ú
        else if (line.includes('ÌïôÎ™Ö:')) {
          plantData.scientificName = line.split(':')[1].trim();
        }
        // Î¨ºÏ£ºÍ∏∞ Ï∂îÏ∂ú
        else if (line.toLowerCase().includes('Î¨ºÏ£ºÍ∏∞:')) {
          plantData.waterFrequency = line.split(':')[1].trim();
        }
        // Ïò®ÎèÑ Ï∂îÏ∂ú
        else if (line.includes('Ïò®ÎèÑ:')) {
          plantData.temperature = line.split(':')[1].trim();
        }
        // ÏäµÎèÑ Ï∂îÏ∂ú
        else if (line.includes('ÏäµÎèÑ:')) {
          plantData.humidity = line.split(':')[1].trim();
        }
        // ÌäπÏßï ÏÑπÏÖò ÏãúÏûë
        else if (line.includes('ÌäπÏßï:')) {
          currentSection = 'features';
        }
        // Ï£ºÏùòÏÇ¨Ìï≠ ÏÑπÏÖò ÏãúÏûë
        else if (line.includes('Ï£ºÏùòÏÇ¨Ìï≠:')) {
          currentSection = 'precautions';
        }
        // ÌäπÏßïÏù¥ÎÇò Ï£ºÏùòÏÇ¨Ìï≠ Ìï≠Î™© Ï∂îÍ∞Ä
        else if (line && currentSection) {
          if (line.startsWith('-') || line.startsWith('‚Ä¢')) {
            line = line.substring(1).trim();
          }
          if (line && currentSection === 'features') {
            plantData.features.push(line);
          } else if (line && currentSection === 'precautions') {
            plantData.precautions.push(line);
          }
        }
      });

      return plantData;
    } catch (error) {
      console.error('Parsing error:', error);
      throw new Error('ÏãùÎ¨º Ï†ïÎ≥¥ ÌååÏã± Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    }
  };

  const handleSubmit = async (e) => {
    if (e) e.preventDefault();
    
    if (!selectedImage) {
      setError('Ïù¥ÎØ∏ÏßÄÎ•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî');
      return;
    }
    
    if (!API_KEY) {
      setError('API ÌÇ§Í∞Ä ÏÑ§Ï†ïÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§');
      return;
    }

    setIsLoading(true);
    setResult('Ïù¥ÎØ∏ÏßÄ Î∂ÑÏÑù Ï§ë...');
    setError(null);

    try {
      const reader = new FileReader();
      reader.readAsDataURL(selectedImage);
      
      reader.onloadend = async () => {
        try {
          const base64Image = reader.result.split(',')[1];
          
          const requestBody = {
            contents: [{
              parts: [
                {
                  text: `Ïù¥ ÏãùÎ¨ºÏùò Ï†ïÎ≥¥Î•º Îã§Ïùå ÌòïÏãùÏúºÎ°ú ÏïåÎ†§Ï£ºÏÑ∏Ïöî:
Ïù¥Î¶Ñ:
ÌïôÎ™Ö:
Î¨ºÏ£ºÍ∏∞:
Ïò®ÎèÑ:
ÏäµÎèÑ:
ÌäπÏßï:
- ÌäπÏßï1
- ÌäπÏßï2
Ï£ºÏùòÏÇ¨Ìï≠:
- Ï£ºÏùòÏÇ¨Ìï≠1
- Ï£ºÏùòÏÇ¨Ìï≠2`
                },
                {
                  inlineData: {
                    mimeType: "image/jpeg",
                    data: base64Image
                  }
                }
              ]
            }]
          };

          const response = await fetch(
            `https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash-latest:generateContent?key=${API_KEY}`,
            {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
              },
              body: JSON.stringify(requestBody)
            }
          );

          if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
          }

          const data = await response.json();
          
          if (data.candidates && data.candidates[0] && data.candidates[0].content) {
            const text = data.candidates[0].content.parts[0].text;
            console.log('API Response:', text);
            
            const plantData = parsePlantInfo(text);
            setPlantInfo(plantData);
            setResult(null);
          } else {
            throw new Error('API ÏùëÎãµ ÌòïÏãùÏù¥ Ïò¨Î∞îÎ•¥ÏßÄ ÏïäÏäµÎãàÎã§.');
          }
          
        } catch (error) {
          console.error('Error:', error);
          setError(`Ïù¥ÎØ∏ÏßÄ Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§: ${error.message}`);
          setPlantInfo(null);
        }
      };
    } catch (error) {
      console.error('Error:', error);
      setError('Ïù¥ÎØ∏ÏßÄ Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
      setPlantInfo(null);
    } finally {
      setIsLoading(false);
    }
  };

  const PlantCard = ({ name, scientificName, waterFrequency, temperature, humidity, difficulty, features, precautions }) => {
    return (
      <div className="card">
        <h2>üåø {name} <span style={{ fontSize: '14px', color: '#888' }}>({difficulty})</span></h2>
        {scientificName !== "Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§" && <h3>{scientificName}</h3>}
        <div className="tags">
          <span className="tag related">Í¥ÄÏóΩÏãùÎ¨º</span>
          <span className="tag level">Ï¥àÍ∏â</span>
        </div>
        <div className="info-section">
