import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [result, setResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  
  const API_KEY = process.env.REACT_APP_GEMINI_API_KEY;

  useEffect(() => {
    if (selectedImage) {
      handleSubmit();
    }
  }, [selectedImage]);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedImage(file);
      setResult(null);
    }
  };

  const handleSubmit = async (e) => {
    if (e) e.preventDefault();
    
    if (!selectedImage) {
      alert('이미지를 선택해주세요');
      return;
    }
    
    if (!API_KEY) {
      alert('API 키가 설정되지 않았습니다');
      return;
    }

    setIsLoading(true);
    setResult('이미지 분석 중...');

    try {
      const reader = new FileReader();
      reader.readAsDataURL(selectedImage);
      
      reader.onloadend = async () => {
        try {
          const base64Image = reader.result.split(',')[1];
          const cleanBase64 = btoa(atob(base64Image));
          
          const requestBody = {
            contents: [{
              parts: [
                {
                  text: "이 식물이나 꽃의 이름을 작은따옴표로 감싸서 한국어로 알려주세요. 예시) '동백꽃'"
                },
                {
                  inlineData: {
                    mimeType: "image/jpeg",
                    data: cleanBase64
                  }
                }
              ]
            }]
          };

          const response = await fetch(
            `https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash-latest:generateContent?key=${API_KEY}`,
            {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
              },
              body: JSON.stringify(requestBody)
            }
          );

          if (!response.ok) {
            const errorData = await response.text();
            throw new Error(`HTTP error! status: ${response.status}, details: ${errorData}`);
          }

          const data = await response.json();
          const text = data.candidates[0].content.parts[0].text;
          // ** 를 ' 로 변경
          const modifiedText = text.replace(/\*\*(.*?)\*\*/g, "'$1'");
          setResult(modifiedText);
          
        } catch (error) {
          console.error('Error:', error);
          setResult(`이미지 처리 중 오류가 발생했습니다: ${error.message}`);
        }
      };
    } catch (error) {
      console.error('Error:', error);
      setResult('이미지 처리 중 오류가 발생했습니다.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>Plant Identifier</h1>
      
      <form onSubmit={handleSubmit}>
        <input
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
          disabled={isLoading}
        />
        <button type="submit" disabled={isLoading}>
          {isLoading ? '분석 중...' : '분석하기'}
        </button>
      </form>

      {selectedImage && (
        <div className="preview">
          <h3>선택된 이미지:</h3>
          <img
            src={URL.createObjectURL(selectedImage)}
            alt="선택된 식물"
            style={{ maxWidth: '300px' }}
          />
        </div>
      )}

      {result && (
        <div className="result">
          <h3>분석 결과:</h3>
          <p>{result}</p>
        </div>
      )}
    </div>
  );
}

export default App;
